import "../App.css";
import React, { useEffect, useState } from "react";
import { Container, Nav, Navbar } from "react-bootstrap";

function Mynavbar(props) {
  const [showToday, setShowToday] = useState(true);

  useEffect(() => {
    //console.log("use effect: " + showToday);
    if (showToday) props.setShowToday(true);
    else props.setShowToday(false);
  });

  let todayFontWeight =
    showToday && !props.openSignIn
      ? { fontWeight: "bold" }
      : { fontWeight: "normal" };

  let forecastFontWeight = showToday
    ? { fontWeight: "normal" }
    : { fontWeight: "bold" };

  let signInFontWeight = props.openSignIn
    ? { fontWeight: "bold" }
    : { fontWeight: "normal" };

  return (
    <div>
      <Navbar bg="light" sticky="top">
        <Container>
          <Navbar.Brand>WeatherMood</Navbar.Brand>
          <Nav className="me-auto">
            <div className="navbarLinks">
              <div className="navbarLeft">
                <div
                  className="navItem"
                  style={todayFontWeight}
                  onClick={() => {
                    if (!props.openSignIn) setShowToday(true);
                  }}
                >
                  Today
                </div>
                <div
                  className="navItem"
                  style={forecastFontWeight}
                  onClick={() => {
                    if (!props.openSignIn) setShowToday(false);
                  }}
                >
                  Forcast
                </div>
              </div>
              <div className="navbarRight">
                <div
                  className="navItem"
                  style={signInFontWeight}
                  onClick={() => {
                    props.setOpenSignIn(true);
                  }}
                >
                  Sign In
                </div>
                <div className="navItem">Sign Up</div>
              </div>
            </div>
          </Nav>
        </Container>
      </Navbar>
    </div>
  );
}

export default Mynavbar;
