import "../App.css";
import React, { useState } from "react";
import Axios from "axios";

const APIkey = "029ab88b8a27d39ef20a062c38c4b411";

function Today() {
  const [location, setLocation] = useState("Taipei");
  const [lon, setLon] = useState(0);
  const [lat, setLat] = useState(0);
  const [temp, setTemp] = useState(0);
  const [id, setId] = useState(0);
  const [weather, setWeather] = useState("clear");

  const getLocation = () => {
    Axios.get(
      "http://api.openweathermap.org/geo/1.0/direct?q=" +
        location +
        "&limit=5&appid=" +
        APIkey
    ).then((response) => {
      console.log("location: " + location);
      setLon(response.data[0].lon);
      setLat(response.data[0].lat);
      getWeather();
    });
  };

  const getWeather = () => {
    Axios.get(
      "http://api.openweathermap.org/data/2.5/weather?lat=" +
        lat +
        "&lon=" +
        lon +
        "&appid=" +
        APIkey
    ).then((response) => {
      setTemp(response.data.main.temp);
      setId(response.data.weather[0].id);
      console.log("temp: " + temp);
      checkWeather();
      console.log("weather: " + weather);
    });
  };

  const checkWeather = () => {
    switch (id) {
      case id < 300:
        // thunderstorm
        setWeather("thunderstorm");
        break;
      case id >= 300 && id < 600:
        // rain
        setWeather("rain");
        break;
      case id >= 600 && id < 700:
        // rain
        setWeather("rain");
        break;
      case id >= 700 && id < 800:
        // rain
        setWeather("rain");
        break;
      case id >= 800:
        // rain
        setWeather("rain");
        break;
      default:
        setWeather("clear");
    }
  };

  return (
    <div>
      <input
        value={location}
        onChange={(event) => setLocation(event.target.value)}
      />
      <button onClick={getLocation}>Check</button>
      <p>{weather}</p>
      <p>{temp}</p>
      <p>{location}</p>
    </div>
  );
}

export default Today;
