import "./App.css";
import Mynavbar from "./Components/Mynavbar";
import Today from "./Pages/Today";
import Forecast from "./Pages/Forecast";
import ErrorPage from "./Pages/ErrorPage";
import React, { useEffect, useState } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import { Alert } from "bootstrap";
import Axios from "axios";

const APIkey = "029ab88b8a27d39ef20a062c38c4b411";

function App() {
  const [date, setDate] = useState(0);
  const dayList = ["Sun", "Mon", "Tue", "Wed", "Thur", "Fri", "Sat"];

  const [todayWeather, setTodayWeather] = useState({
    location: "Taipei",
    unit: "metric",
    temp: 0,
    graph: "°C",
    weather: "Clear",
  });
  console.log(todayWeather);
  const [forecast, setForecast] = useState({
    0: {
      id: 0,
      weather: "Clear",
      temp: 0,
      logo: "url(/clearLogo.png)",
      icon: "url(/clearMini.png)",
    },
    1: {
      id: 0,
      weather: "Clear",
      temp: 0,
      logo: "url(/clearLogo.png)",
      icon: "url(/clearMini.png)",
    },
    2: {
      id: 0,
      weather: "Clear",
      temp: 0,
      logo: "url(/clearLogo.png)",
      icon: "url(/clearMini.png)",
    },
    3: {
      id: 0,
      weather: "Clear",
      temp: 0,
      logo: "url(/clearLogo.png)",
      icon: "url(/clearMini.png)",
    },
  });
  const [logo, setLogo] = useState({ backgroundImage: "url(/clearLogo.png)" });
  const [background, setBackground] = useState({
    backgroundImage: "url(/clearSky.jpg)",
  });

  useEffect(() => {
    getLocation();
    getDate();
  }, []);

  const getDate = () => {
    var day = new Date();
    setDate(day.getDay());
    //console.log(today);
  };

  const getLocation = () => {
    if (navigator.geolocation)
      navigator.geolocation.getCurrentPosition(getLonandLat);
    else Alert("Geolocation is not supported!");
  };

  function getLonandLat(position) {
    initialWeather(position.coords.longitude, position.coords.latitude);
    initialForecast(position.coords.longitude, position.coords.latitude);
  }

  const initialWeather = (lon, lat) => {
    // this will render initial page when user get in website for the first time
    Axios.get(
      "https://api.openweathermap.org/data/2.5/weather?lat=" +
        lat +
        "&lon=" +
        lon +
        "&units=" +
        todayWeather.unit +
        "&appid=" +
        APIkey
    )
      .then((response) => {
        //console.log(response.data);
        //console.log(response.data.weather[0].main);
        console.log("here");
        setTodayWeather((prevState) => ({
          ...prevState,
          graph: todayWeather.unit === "metric" ? "°C" : "°F",
          temp: parseInt(response.data.main.temp),
          location: response.data.name,
        }));
        checkWeather(response.data.weather[0].id);
      })
      .catch((error) => {
        alert("City Not Found!");
      });
  };

  const initialForecast = (lon, lat) => {
    Axios.get(
      "https://api.openweathermap.org/data/2.5/forecast?lat=" +
        lat +
        "&lon=" +
        lon +
        "&units=" +
        todayWeather.unit +
        "&appid=" +
        APIkey
    ).then((response) => {
      setForecast({
        0: { temp: response.data.list[8].main.temp },
        1: { temp: response.data.list[16].main.temp },
        2: { temp: response.data.list[24].main.temp },
        3: { temp: response.data.list[32].main.temp },
      });
      checkForecastWeather([
        response.data.list[8].weather[0].id,
        response.data.list[16].weather[0].id,
        response.data.list[24].weather[0].id,
        response.data.list[32].weather[0].id,
      ]);
    });
  };

  function checkForecastWeather(ID) {
    setForecast({
      0: { id: ID[0], weather: setForecastPic(ID[0], 0) },
      1: { id: ID[1], weather: setForecastPic(ID[1], 1) },
      2: { id: ID[2], weather: setForecastPic(ID[2], 2) },
      3: { id: ID[3], weather: setForecastPic(ID[3], 3) },
    });
  }

  function setForecastPic(ID, index) {
    if (ID >= 200 && ID < 300) {
      setForecast({
        index: { logo: "url(/thunderstormLogo.png)" },
        icon: "url(/thunderstormMini.png)",
      });
      return "Tunderstorm";
    }
    if (ID >= 300 && ID < 600) {
      setForecast({
        index: { logo: "url(/rainLogo.png)" },
        icon: "url(/rainMini.png)",
      });
      return "Rain";
    }
    if (ID >= 600 && ID < 700) {
      setForecast({
        index: { logo: "url(/snowLogo.png)" },
        icon: "url(/snowMini.png)",
      });
      return "Snow";
    }
    if (ID >= 700 && ID < 800) {
      setForecast({
        index: { logo: "url(/mistLogo.png)" },
        icon: "url(/mistMini.png)",
      });
      return "Mist";
    } else {
      setForecast({
        index: { logo: "url(/clearLogo.png)" },
        icon: "url(/clearMini.png)",
      });
      return "Clear";
    }
  }

  const getWeather = () => {
    //console.log("int today.getWeather: " + unit);
    Axios.get(
      "https://api.openweathermap.org/data/2.5/weather?q=" +
        todayWeather.location +
        "&units=" +
        todayWeather.unit +
        "&appid=" +
        APIkey
    )
      .then((response) => {
        //console.log(response.data);
        //console.log(response.data.weather[0].main);

        setTodayWeather((todayWeather) => ({
          graph: todayWeather.unit === "metric" ? "°C" : "°F",
          temp: parseInt(response.data.main.temp),
        }));

        checkWeather(response.data.weather[0].id);
      })
      .catch((error) => {
        alert("City Not Found!");
      });
  };

  const checkWeather = (weatherID) => {
    if (weatherID >= 200 && weatherID < 300) {
      setTodayWeather((todayWeather) => ({
        weather: "Thunderstorm",
      }));
      setBackground({ backgroundImage: "url(/thunderstorm.jpg)" });
      setLogo({ backgroundImage: "url(/thunderstormLogo.png)" });
    } else if (weatherID >= 300 && weatherID < 600) {
      setTodayWeather((todayWeather) => ({
        weather: "Rain",
      }));
      setBackground({ backgroundImage: "url(/rain.jpg)" });
      setLogo({ backgroundImage: "url(/rainLogo.png)" });
    } else if (weatherID >= 600 && weatherID < 700) {
      setTodayWeather((todayWeather) => ({
        weather: "Snow",
      }));
      setBackground({ backgroundImage: "url(/snow.jpg)" });
      setLogo({ backgroundImage: "url(/snowLogo.png)" });
    } else if (weatherID >= 700 && weatherID < 800) {
      setTodayWeather((todayWeather) => ({
        weather: "Mist",
      }));
      setBackground({ backgroundImage: "url(/mist.jpg)" });
      setLogo({ backgroundImage: "url(/mistLogo.png)" });
    } else {
      setTodayWeather((todayWeather) => ({
        weather: "Clear",
      }));
      setBackground({ backgroundImage: "url(/clearSky.jpg)" });
      setLogo({ backgroundImage: "url(/clearLogo.png)" });
    }
  };

  return (
    <div className="App">
      <Router>
        <Mynavbar />
        <Routes>
          <Route
            path="/today"
            element={
              <Today
                background={background}
                logo={logo}
                todayWeather={todayWeather}
                getWeather={getWeather}
                setTodayWeather={setTodayWeather}
              />
            }
          />
          <Route
            path="/forecast"
            element={
              <Forecast
                todayWeather={todayWeather}
                date={date}
                dayList={dayList}
                background={background}
                forecast={forecast}
              />
            }
          />
          <Route path="*" element={<ErrorPage />} />
        </Routes>
      </Router>
    </div>
  );
}

export default App;
