import "../App.css";
import React, { useState, Select } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faLocationDot } from "@fortawesome/free-solid-svg-icons";

function PostBar(props) {
  const [mood, setMood] = useState("Clear");

  const options = [
    { value: "text", label: <FontAwesomeIcon icon={faFileAlt} /> },
    { value: "image", label: <FontAwesomeIcon icon={faFileImage} /> },
    { value: "video", label: <FontAwesomeIcon icon={faFileVideo} /> },
  ];

  return (
    <div className="PostBar">
      <Select placeholder="Text" options={options} />
      <select
        className="PostBarMood"
        value={mood}
        onChange={(event) => {
          setMood(event.target.value);
        }}
      >
        <option value="Clear" className="moods">
          <FontAwesomeIcon icon={faLocationDot} />
          {mood}
        </option>
        <option value="Rain" className="moods">
          <FontAwesomeIcon icon={faLocationDot} />
          Rain
        </option>
        <option value="Cloud" className="moods">
          <FontAwesomeIcon icon={faLocationDot} />
          Cloud
        </option>
        <option value="Thunder" className="moods">
          <FontAwesomeIcon icon={faLocationDot} />
          Thunder
        </option>
        <option value="Mist" className="moods">
          <FontAwesomeIcon icon={faLocationDot} />
          Mist
        </option>
        <option value="Snow" className="moods">
          <FontAwesomeIcon icon={faLocationDot} />
          Snow
        </option>
      </select>
      <input
        className="PostBarContent"
        placeholder="What's on your mind?"
      ></input>
      <button className="PostBarPost">
        <FontAwesomeIcon icon={faLocationDot} />
        Post
      </button>
    </div>
  );
}

export default PostBar;
