import "../App.css";
import WeatherMood from "./WeatherMood";
import React, { Fragment, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faLocationDot, faSun } from "@fortawesome/free-solid-svg-icons";
import Select from "react-select";

function PostBar(props) {
  const [mood, setMood] = useState("");
  const [content, setContent] = useState("");
  const [moodList, setMoodList] = useState([]);

  const options = [
    {
      value: "Clear",
      label: (
        <>
          <FontAwesomeIcon icon={faSun} />
          <span className="PostBarSelectSpan">Clear</span>
        </>
      ),
    },
    {
      value: "Cloud",
      label: (
        <>
          <FontAwesomeIcon icon={faLocationDot} />
          <span>Cloud</span>
        </>
      ),
    },
    {
      value: "Thunder",
      label: (
        <>
          <FontAwesomeIcon icon={faLocationDot} />
          <span>Thunder</span>
        </>
      ),
    },
    {
      value: "Rain",
      label: (
        <>
          <FontAwesomeIcon icon={faLocationDot} />
          <span>Rain</span>
        </>
      ),
    },
    {
      value: "Mist",
      label: (
        <>
          <FontAwesomeIcon icon={faLocationDot} />
          <span>Mist</span>
        </>
      ),
    },
    {
      value: "Snow",
      label: (
        <>
          <FontAwesomeIcon icon={faLocationDot} />
          <span>Snow</span>
        </>
      ),
    },
  ];

  const selectPlacholder = "Mood";

  const handleSelect = (event) => {
    setMood(event.value);
  };

  const postMood = () => {
    setMoodList([...moodList, { content: content, mood: mood }]);
  };

  return (
    <div className="PostBar">
      <div className="PostBarInput">
        <Select
          placeholder={selectPlacholder}
          options={options}
          className="PostBarMood"
          isSearchable={false}
          onChange={(event) => handleSelect(event)}
        />
        <textarea
          className="PostBarContent"
          placeholder="What's on your mind?"
          onChange={(event) => {
            setContent(event.target.value);
          }}
        ></textarea>
        <button className="PostBarButton" onClick={postMood}>
          Post
        </button>
      </div>
      <div className="PostBarSpace"></div>
      <div className="PostBarOutput">
        {moodList.map((list, index) => {
          return (
            <WeatherMood content={list.content} mood={list.mood} key={index} />
          );
        })}
      </div>
    </div>
  );
}

export default PostBar;
