import "../App.css";
import React from "react";

function Searchbar(props) {
  // props -> weatherInfo
  return (
    <div className="Searchbar">
      <input
        value={props.location}
        onChange={(event) => props.setLocation(this.value)}
      />
      <button onClick={props.getWeather}>Check</button>
      <p>{props.weather}</p>
      <p>{props.temp}</p>
      <p>{props.location}</p>
    </div>
  );
}

export default Searchbar;
