import "../App.css";
import Showinfo from "../Components/Showinfo";
import Searchbar from "../Components/Searchbar";
import React, { useState } from "react";
import Axios from "axios";

const APIkey = "029ab88b8a27d39ef20a062c38c4b411";

function Today() {
  const [location, setLocation] = useState("Taipei");
  const [temp, setTemp] = useState(0);
  const [id, setId] = useState(0);
  const [weather, setWeather] = useState("clear");
  const [background, setBackground] = useState({
    backgroundImage: "url(/clearSky.jpg)",
  });

  const getWeather = () => {
    Axios.get(
      "https://api.openweathermap.org/data/2.5/weather?q=" +
        location +
        "&appid=" +
        APIkey
    ).then((response) => {
      setTemp(response.data.main.temp);
      setId(response.data.weather[0].id);
      checkWeather();
      console.log(response.data.name);
      console.log(response.data.weather[0].id);
      console.log(response.data.weather[0].main);
      console.log(response.data.main.temp);
      console.log(" ");
    });
  };

  const checkWeather = () => {
    if (id >= 200 && id < 300) {
      setWeather("thunderstorm");
      setBackground({ backgroundImage: "url(/thunderstorm.jpg)" });
    } else if (id >= 300 && id < 600) {
      setWeather("rain");
      setBackground({ backgroundImage: "url(/rain.jpg)" });
    } else if (id >= 600 && id < 700) {
      setWeather("snow");
      setBackground({ backgroundImage: "url(/snow.jpg)" });
    } else if (id >= 700 && id < 800) {
      setWeather("mist");
      setBackground({ backgroundImage: "url(/mist.jpg)" });
    } else {
      setWeather("clear");
      setBackground({ backgroundImage: "url(/clearSky.jpg)" });
    }
  };

  return (
    <div style={background} className="today">
      <Showinfo />
      <Searchbar
        location={location}
        setLocation={setLocation}
        getWeather={getWeather}
      />
      <p>{weather}</p>
      <p>{temp}</p>
      <p>{location}</p>
    </div>
  );
}

export default Today;
