import "../App.css";
import WeatherMood from "./WeatherMood";
import React, { Fragment, useState } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faLocationDot } from "@fortawesome/free-solid-svg-icons";
import Select from "react-select";

function PostBar(props) {
  const [mood, setMood] = useState("");
  const [content, setContent] = useState("");
  const [moodList, setMoodList] = useState([]);

  const options = [
    { value: "Clear", label: "Clear" },
    { value: "Cloud", label: "Cloud" },
    { value: "Thunder", label: "Thunder" },
    { value: "Rain", label: "Rain" },
    { value: "Mist", label: "Mist" },
    { value: "Snow", label: "Snow" },
  ];

  const selectPlacholder = "Mood";

  const handleSelect = (event) => {
    setMood(event.value);
  };

  const postMood = () => {
    setMoodList([...moodList, { content: content, mood: mood }]);
  };

  return (
    <div className="PostBar">
      <div className="PostBarOutput">
        {moodList.map((list, index) => {
          return (
            <WeatherMood content={list.content} mood={list.mood} key={index} />
          );
        })}
      </div>
    </div>
  );
}

export default PostBar;
