import "../App.css";
import React, { useState } from "react";

function Searchbar(props) {
  const [tmp, setTmp] = useState("°C");

  function handleGraph(event) {
    console.log(event.target.value);
    setTmp(event.target.value);
  }

  function handleClick() {
    props.setUnit(tmp);
    props.getWeather();
  }

  return (
    <div className="Searchbar">
      <input
        className="SearchbarLocation"
        value={props.location}
        onChange={(event) => props.setLocation(event.target.value)}
      />
      <select
        className="SearchbarSelect"
        value={tmp === "metric" ? "°C" : "°F"}
        onChange={(event) => handleGraph(event)}
      >
        <option value="metric">°C</option>
        <option value="imperial">°F</option>
      </select>
      <button className="SearchbarButton" onClick={handleClick}>
        Check
      </button>
    </div>
  );
}

export default Searchbar;
