import "../App.css";
import React, { useEffect, useState } from "react";

function Loading() {
  const delay = 300;
  const [loadingVisible, setLoadingVisible] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      setLoadingVisible(false);
    }, delay);
  }, []);

  return loadingVisible ? <div className="Loading"></div> : <div></div>;
}

export default Loading;
