import "../App.css";
import React, { useState } from "react";
import { wait } from "@testing-library/user-event/dist/utils";

function Searchbar(props) {
  return (
    <div className="Searchbar">
      <input
        className="SearchbarLocation"
        value={props.todayWeather.location}
        //onChange={(event) => props.setLocation(event.target.value)}
        onChange={(event) =>
          props.setTodayWeather({
            location: event.target.value,
            wether: props.todayWeather.wether,
            unit: props.todayWeather.unit,
            temp: props.todayWeather.temp,
            graph: props.todayWeather.graph,
          })
        }
        placeholder="City Name"
      />
      <select
        className="SearchbarSelect"
        value={props.unit}
        onChange={(event) => props.setUnit(event.target.value)}
      >
        <option value="metric">°C</option>
        <option value="imperial">°F</option>
      </select>
      <button className="SearchbarButton" onClick={props.getWeather}>
        Check
      </button>
    </div>
  );
}

export default Searchbar;
