import "../App.css";
import React, { useEffect, useState } from "react";

function Showinfo(props) {
  const [logo, setLogo] = useState({
    backgroundImage: "url(/clearLogo.png)",
  });

  useEffect =
    (() => {
      checkWeather();
    },
    []);

  const checkWeather = () => {
    console.log(props.weather);
    if (props.weather === "thunderstorm")
      setLogo({ backgroundImage: "url(/thunderstormLogo.png)" });
    else if (props.weather === "rain")
      setLogo({ backgroundImage: "url(/rainLogo.png)" });
    else if (props.weather === "snow")
      setLogo({ backgroundImage: "url(/snowLogo.png)" });
    else if (props.weather === "mist")
      setLogo({ backgroundImage: "url(/mistLogo.png)" });
    else setLogo({ backgroundImage: "url(/clearLogo.png)" });
  };

  return <div className="Showinfo" style={logo}></div>;
}

export default Showinfo;
