import React from "react";
import { Formik, Form, Field, ErrorMessage } from "formik";
import * as Yup from "yup";

function SignIn(props) {
  const handleLeaveClick = () => {
    props.setOpenSignIn(false);
    props.setOpenSignUp(false);
  };

  const initialValue = {
    username: "",
    password: "",
  };

  const validationSchema = Yup.object().shape({
    username: Yup.string().min(3).max(10).required(),
    password: Yup.string().min(3).max(10).required(),
  });

  const onSubmit = (data) => {
    console.log(data);
  };

  return (
    <div className="Sign">
      <Formik
        initialValues={initialValue}
        onSubmit={onSubmit}
        validationSchema={validationSchema}
      >
        <Form className="SignArea">
          <ErrorMessage name="username" component="span"></ErrorMessage>
          <Field
            className="SignUsername"
            name="username"
            placeholder="Username..."
          ></Field>
          <Field
            className="SignPassword"
            name="password"
            placeholder="Password..."
          ></Field>
          <button
            className="SignButtons"
            onClick={() => {
              props.setOpenSignIn(false);
            }}
          >
            Sign In
          </button>
        </Form>
      </Formik>

      {/*<div className="SignArea">
        <button
          className="SignLeave"
          onClick={() => {
            handleLeaveClick();
          }}
        >
          X
        </button>
        <input className="SignUsername" placeholder="Username..."></input>
        <input className="SignPassword" placeholder="Password..."></input>
        <div className="SignButtons">
          <button
            onClick={() => {
              props.setOpenSignIn(false);
            }}
          >
            Sign In
          </button>
        </div>
      </div>*/}
    </div>
  );
}

export default SignIn;
