import "../App.css";
import React, { useState } from "react";
import Axios from "axios";

const APIkey = "029ab88b8a27d39ef20a062c38c4b411";

function Today() {
  const [location, setLocation] = useState("Taipei");
  const [temp, setTemp] = useState(0);
  const [id, setId] = useState(0);
  const [weather, setWeather] = useState("clear");

  const getWeather = () => {
    Axios.get(
      "https://api.openweathermap.org/data/2.5/weather?q=" +
        location +
        "&appid=" +
        APIkey
    ).then((response) => {
      console.log(response.data);
      setTemp(response.data.main.temp);
      setId(response.data.weather[0].id);
      checkWeather();
    });
  };

  const checkWeather = () => {
    if (id < 300) setWeather("thunderstorm");
    else if (id >= 300 && id < 600) setWeather("rain");
    else if (id >= 600 && id < 700) setWeather("snow");
    else if (id >= 700 && id < 800) setWeather("mist");
    else setWeather("clear");
  };

  return (
    <div>
      <input
        value={location}
        onChange={(event) => setLocation(event.target.value)}
      />
      <button onClick={getWeather}>Check</button>
      <p>{weather}</p>
      <p>{temp}</p>
      <p>{location}</p>
    </div>
  );
}

export default Today;
