import "../App.css";
import React, { useState } from "react";
import { wait } from "@testing-library/user-event/dist/utils";

function Searchbar(props) {
  let newLocation;
  let nowLocation = props.todayWeather.location;

  const handleText = (text) => {
    newLocation = text;
    console.log(newLocation);
  };

  const handleButton = () => {
    props.setTodayWeather({
      location: newLocation,
      weather: props.todayWeather.wether,
      unit: props.todayWeather.unit,
      temp: props.todayWeather.temp,
      graph: props.todayWeather.graph,
    });

    props.setTodayWeather({
      location: props.todayWeather.location,
      weather: props.todayWeather.wether,
      unit: props.todayWeather.unit,
      temp: props.todayWeather.temp,
      graph: props.todayWeather.graph,
    });

    props.getWeather();
  };

  return (
    <div className="Searchbar">
      <input
        className="SearchbarLocation"
        //value={props.todayWeather.location}
        value={newLocation}
        //onChange={(event) => props.setLocation(event.target.value)}
        /*onChange={(event) =>
          props.setTodayWeather({
            location: event.target.value,
            wether: props.todayWeather.wether,
            unit: props.todayWeather.unit,
            temp: props.todayWeather.temp,
            graph: props.todayWeather.graph,
          })
        }*/
        onChange={(event) => handleText(event.target.value)}
        placeholder={nowLocation}
      />
      <select
        className="SearchbarSelect"
        value={props.unit}
        onChange={(event) => props.setUnit(event.target.value)}
      >
        <option value="metric">°C</option>
        <option value="imperial">°F</option>
      </select>
      <button className="SearchbarButton" onClick={handleButton}>
        Check
      </button>
    </div>
  );
}

export default Searchbar;
