import "../App.css";
import "./clearSky.jpg";
import React, { useState } from "react";
import Axios from "axios";

const APIkey = "029ab88b8a27d39ef20a062c38c4b411";

function Today() {
  const [location, setLocation] = useState("Taipei");
  const [temp, setTemp] = useState(0);
  const [id, setId] = useState(0);
  const [weather, setWeather] = useState("clear");
  const [background, setBackground] = useState({
    backgroundImage: "./img/clearSky.jpg",
  });

  const myStyle = {
    backgroundImage: "url(./clearSky.jpg)",
  };

  const getWeather = () => {
    Axios.get(
      "https://api.openweathermap.org/data/2.5/weather?q=" +
        location +
        "&appid=" +
        APIkey
    ).then((response) => {
      console.log(response.data);
      setTemp(response.data.main.temp);
      setId(response.data.weather[0].id);
      checkWeather();
    });
  };

  const checkWeather = () => {
    if (id < 300) {
      setWeather("thunderstorm");
      setBackground({ backgroundImage: "url(./img/thunderstorm.jpg)" });
    } else if (id >= 300 && id < 600) {
      setWeather("rain");
      setBackground({ backgroundImage: "url(./img/rain.jpg)" });
    } else if (id >= 600 && id < 700) {
      setWeather("snow");
      setBackground({ backgroundImage: "url(./img/snow.jpg)" });
    } else if (id >= 700 && id < 800) {
      setWeather("mist");
      setBackground({ backgroundImage: "url(./img/mist.jpg)" });
    } else {
      setWeather("clear");
      setBackground({ backgroundImage: "url(./img/clearSky.jpg)" });
    }
  };

  return (
    <div style={{ backgroundImage: "url('clearSky.png')" }}>
      <input
        value={location}
        onChange={(event) => setLocation(event.target.value)}
      />
      <button onClick={getWeather}>Check</button>
      <p>{weather}</p>
      <p>{temp}</p>
      <p>{location}</p>
    </div>
  );
}

export default Today;
