import "../App.css";
import React from "react";
import Loading from "../Components/Loading";

function Forecast(props) {
  //console.log(props.forecast);
  let future = [props.date + 2, props.date + 3, props.date + 4];
  for (var i = 0; i < 3; i++) if (future[i] > 6) future[i] -= 7;

  let icon0, icon1, icon2;
  setIcon(icon0, 1);
  setIcon(icon1, 2);
  setIcon(icon2, 3);
  function setIcon(icon, index) {
    let ID = props.forecast.id[index];
    if (ID >= 200 && ID < 300) icon = "/thunderstormIcon.png";
    else if (ID >= 300 && ID < 600) icon = "/rainIcon.png";
    else if (ID >= 600 && ID < 700) icon = "/snowIcon.png";
    else if (ID >= 700 && ID < 800) icon = "/mistIcon.png";
    else if (ID === 800) icon = "/clearIcon.png";
    else icon = "/cloudIcon.png";
  }

  return (
    <div>
      <div>
        <Loading />
      </div>
      <div style={props.background} className="Background"></div>
      <div className="forecast">
        <div className="tomorrow" style={props.forecastLogo}>
          <p className="tomorrowWeather">
            Tomorrow: {props.forecast.weather[0]}
          </p>
          <p className="tomorrowTemp">
            {props.forecast.temp[0]}
            {props.todayWeather.graph}
          </p>
        </div>
        <div className="futureDays">
          <div>
            {props.dayList[future[0]]}: {props.forecast.temp[1]}°
            <img src="/clearIcon.png" alt="clear" className="futureIcon"></img>
          </div>
          <div>
            {props.dayList[future[1]]}: {props.forecast.temp[2]}°
            <img src="/clearIcon.png" alt="clear" className="futureIcon"></img>
          </div>
          <div>
            {props.dayList[future[2]]}: {props.forecast.temp[3]}°
            <img src={icon0} alt="clear" className="futureIcon"></img>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Forecast;
