import "../App.css";
import React from "react";

function Showinfo(props) {
  let graph = "°C";

  if (props.unit === "metric") graph = "°C";
  else graph = "°F";

  return (
    <div className="Showinfo" style={props.logo}>
      <div className="description">{props.weather}</div>
      <div className="temperature">
        {props.temp}
        {this.state.graph}
      </div>
    </div>
  );
}

export default Showinfo;
