import "../App.css";
import React from "react";

function Searchbar(props) {
  return (
    <div className="Searchbar">
      <input
        className="SearchbarLocation"
        value={props.todayWeather.location}
        onChange={(event) =>
          props.setTodayWeather((prevState) => ({
            ...prevState,
            weather: event.target.value,
          }))
        }
        placeholder="City Name"
      />
      <select
        className="SearchbarSelect"
        value={props.todayWeather.unit}
        onChange={(event) =>
          props.setTodayWeather({ unit: event.target.value })
        }
      >
        <option value="metric">°C</option>
        <option value="imperial">°F</option>
      </select>
      <button className="SearchbarButton" onClick={props.getWeather}>
        Check
      </button>
    </div>
  );
}

export default Searchbar;
